import React, { useEffect, useState } from 'react';
import { useTable, useSortBy, useFilters, usePagination, useRowSelect } from 'react-table';
import { styled } from 'styled-components';


const TH= styled.th`
                    padding: 8px;
                    cursor: pointer;
                    border: 1px solid #E8E8E8;
                    border-Bottom: 1px solid #E8E8E8;
                    background: #E1EDFF;
                    height:56px;
                    color: var(--text-black, #111);
font-feature-settings: 'clig' off, 'liga' off;
font-family: Noto Sans KR;
font-size: 14px;
font-style: normal;
font-weight: 400;
line-height: 20px; /* 142.857% */
letter-spacing: -0.6px;
text-align: center;
                  
`

const TD= styled.td`
                  padding: 8px;
                      border: 1px solid #E8E8E8;
                      border-Bottom: 1px solid #E8E8E8;
                      background: white;
                      height:56px;
                      color: var(--text-black, #111);

/* Admin/Body/B4-14R(Under) */
font-family: Noto Sans KR;
font-size: 14px;
font-style: normal;
font-weight: 400;
line-height: 140%; /* 19.6px */
letter-spacing: -0.56px;


`
function DataTablepopup({ columns, data, setSelectedRowsposting }) {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
      initialState: { pageIndex: 0, pageSize: 9 },
    },
    useFilters,
    useSortBy,
    usePagination,
    useRowSelect,
    (hooks) => {
      hooks.visibleColumns.push((columns) => [
        {
          id: 'selection',
          Header: ({ getToggleAllRowsSelectedProps }) => (
            <div>
              
            </div>
          ),
          Cell: ({ row }) => (
            <div>

            </div>
          ),
        },
        ...columns,
      ]);
    }
  );

  const [checkboxState, setCheckboxState] = useState({});
  const [checkboxChanged, setCheckboxChanged] = useState(false);
/*
  // Update checkbox state when a checkbox is clicked
  const handleCheckboxClick = (row) => {
    const updatedCheckboxState = {
      ...checkboxState,
      [row.original.id]: !checkboxState[row.original.id],
    };
    setCheckboxState(updatedCheckboxState);
    setCheckboxChanged(true); // Indicate that checkbox state has changed
  };
*/
  // Update checkbox state when a checkbox is clicked
  const handleCheckboxClick = (row) => {
    const updatedCheckboxState = {
      ...checkboxState,
      [row.original.id]: {
        checked: !checkboxState[row.original.id]?.checked,
        data: row.original, // Store entire row data
      },
    };
    setCheckboxState(updatedCheckboxState);
    setCheckboxChanged(true); // Indicate that checkbox state has changed
  };
  useEffect(() => {
    // Update selected rows when checkbox state changes
    if (checkboxChanged) {
      const selectedRows = Object.values(checkboxState)
        .filter((entry) => entry.checked)
        .map((entry) => entry.data);
        setSelectedRowsposting(selectedRows);
      setCheckboxChanged(false); // Reset the checkboxChanged state
    }
  }, [checkboxChanged, checkboxState, setSelectedRowsposting]);

  return (
    <div>
      <table {...getTableProps()} style={{ border: '1px solid #E8E8E8', width: '100%', borderCollapse: 'collapse' }}>
        <thead>

          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <TH
                  {...column.getHeaderProps(column.getSortByToggleProps())}

                >
                  {column.render('Header')}
                  {column.isSorted ? (column.isSortedDesc ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                </TH>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>

          {page.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()} style={{ border: '1px solid #E8E8E8' }}>

<td
                  {...row.cells[0].getCellProps()} // Render checkbox in the first cell
                  style={{
                    padding: '8px',
                    border: '1px solid #E8E8E8',
                    borderBottom: '1px solid #E8E8E8',
                    background: 'white',
                  }}
                >
                  <input
                    type="checkbox"
                    checked={checkboxState[row.original.id]?.checked || false}
                    onChange={() => handleCheckboxClick(row)}
                  />
                </td>
                {row.cells.slice(1).map((cell, index) => (
  <TD
    {...cell.getCellProps()}
    key={index}
    style={
      index === 2
        ? { textDecorationLine:'underline' } // 3ë²ˆì§¸ ì»¬ëŸ¼ì— ì›í•˜ëŠ” í°íŠ¸ë¥¼ ì ìš©
        : {}
    }
  >
    {cell.column.id === 'image' ? (
            <img
              src={cell.value} // Assuming the cell value is the image URL
              alt={`Image ${index}`}
              style={{ maxWidth: '100px', maxHeight: '100px' }} // Set image dimensions as needed
            />
          ) : (
            cell.render('Cell')
          )}
  </TD>
))}

              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default DataTablepopup;
