import React, { useEffect, useState } from 'react';
import { useTable, useSortBy, useFilters, usePagination, useRowSelect } from 'react-table';

function DataTablepostend({ columns, data, setSelectedRowspostend }) {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
      initialState: { pageIndex: 0, pageSize: 5, sortBy: [{ id: 'createdAt', desc: true }] },
    },
    useFilters,
    useSortBy,
    usePagination,
    useRowSelect,
    (hooks) => {
      hooks.visibleColumns.push((columns) => [
        {
          id: 'selection',
          Header: ({ getToggleAllRowsSelectedProps }) => (
            <div>
           
            </div>
          ),
          Cell: ({ row }) => (
            <div>

            </div>
          ),
        },
        ...columns,
      ]);
    }
  );

  const [checkboxState, setCheckboxState] = useState({});
  const [checkboxChanged, setCheckboxChanged] = useState(false);
/*
  // Update checkbox state when a checkbox is clicked
  const handleCheckboxClick = (row) => {
    const updatedCheckboxState = {
      ...checkboxState,
      [row.original.id]: !checkboxState[row.original.id],
    };
    setCheckboxState(updatedCheckboxState);
    setCheckboxChanged(true); // Indicate that checkbox state has changed
  };
*/
  // Update checkbox state when a checkbox is clicked
  const handleCheckboxClick = (row) => {
    const updatedCheckboxState = {
      ...checkboxState,
      [row.original.id]: {
        checked: !checkboxState[row.original.id]?.checked,
        data: row.original, // Store entire row data
      },
    };
    setCheckboxState(updatedCheckboxState);
    setCheckboxChanged(true); // Indicate that checkbox state has changed
  };
  useEffect(() => {
    // Update selected rows when checkbox state changes
    if (checkboxChanged) {
      const selectedRows = Object.values(checkboxState)
        .filter((entry) => entry.checked)
        .map((entry) => entry.data);
      setSelectedRowspostend(selectedRows);
      setCheckboxChanged(false); // Reset the checkboxChanged state
    }
  }, [checkboxChanged, checkboxState, setSelectedRowspostend]);

  return (
    <div>
      <table {...getTableProps()} style={{ border: '1px solid #E8E8E8', width: '100%', borderCollapse: 'collapse' }}>
        <thead>

          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th
                  {...column.getHeaderProps(column.getSortByToggleProps())}
                  style={{
                    padding: '8px',
                    cursor: 'pointer',
                    border: '1px solid #E8E8E8',
                    borderBottom: '1px solid #E8E8E8',
                    background: '#E1EDFF',
                  }}
                >
                  {column.render('Header')}
                  {column.isSorted ? (column.isSortedDesc ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>

         
        {page.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()} style={{ border: '1px solid #E8E8E8' }}>

<td
                  {...row.cells[0].getCellProps()} // Render checkbox in the first cell
                  style={{
                    padding: '8px',
                    border: '1px solid #E8E8E8',
                    borderBottom: '1px solid #E8E8E8',
                    background: 'white',
                  }}
                >
                  <input
                    type="checkbox"
                    checked={checkboxState[row.original.id]?.checked || false}
                    onChange={() => handleCheckboxClick(row)}
                  />
                </td>
                {row.cells.slice(1).map((cell, index) => (
                  <td
                    {...cell.getCellProps()}
                    key={index}
                    style={{
                      padding: '8px',
                      border: '1px solid #E8E8E8',
                      borderBottom: '1px solid #E8E8E8',
                      background: 'white',
                    }}
                  >
                    {cell.render('Cell')}
                  </td>
                ))}

              </tr>
            );
          })}

        </tbody>
      </table>
    </div>
  );
}

export default DataTablepostend;
